import React, { useState, useCallback, useEffect } from 'react';
import { Card, Button, Avatar, Form, List, Input, Comment, Popover  } from 'antd';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';

import { EllipsisOutlined, EditOutlined, HeartOutlined, TwitterOutlined } from '@ant-design/icons';

import { ADD_COMMENT_REQUEST, LOAD_COMMENTS_REQUEST, LIKE_POST_REQUEST, 
        UNLIKE_POST_REQUEST, RETWEET_REQUEST, REMOVE_POST_REQUEST } from '../reducers/post';
import { UNFOLLOW_USER_REQUEST, FOLLOW_USER_REQUEST } from '../reducers/user';
import PostImages from './postImages';
import PostCardContent from './postCardContent';


const PostCard = ({ post } ) => {
  const dispatch = useDispatch()
  const { me } = useSelector(state => state.user);
  const { isAddingComment, commentAdded } = useSelector(state => state.post);
  const [ commentFormOpened, setCommentFormOpened ] = useState(false);
  const [ commentText, setCommentText ] = useState('');

  const liked = me && post.Likers && post.Likers.find(v => v.id === me.id)
  useEffect(() => {
    setCommentText('');
  }, [ commentAdded === true]);

  const onSubmitComment = useCallback((e) => {
    e.preventDefault();
    if(!me){
      return alert('you need to login');
    }
    return dispatch({
      type: ADD_COMMENT_REQUEST,
      data: {
        postId: post.id,
        content: commentText,
      }
    })
  }, [ me && me.id, commentText ])

  const onToggleComment = useCallback(() => {
    setCommentFormOpened(prev => !prev);
    if(!commentFormOpened){
      dispatch({
        type: LOAD_COMMENTS_REQUEST,
        data: post.id
      })
    }

  }, [commentFormOpened])

  const onChangeCommentText = useCallback((e) => {
    setCommentText(e.target.value);
  }, [])

  const onLikeOrUnlike = useCallback(() => {
    if(!me){
      alert('No Authentication');
    }
    if(liked){
      dispatch({
        type: UNLIKE_POST_REQUEST,
        data: post.id,
      })
    }else{
      dispatch({
        type: LIKE_POST_REQUEST,
        data: post.id,
      })
    }
  },[me && me.id, post && post.id, liked])

  const onRetweet = useCallback(() => {
    dispatch({
      type: RETWEET_REQUEST,
      data: post.id,
    })
  }, [me && me.id, post && post.id])

  const onUnfollow = useCallback(postUserId => () => {
    dispatch({
      type: UNFOLLOW_USER_REQUEST,
      data: postUserId,
    })
  }, [])

  const onFollow = useCallback(postUserId => () => {
    dispatch({
      type: FOLLOW_USER_REQUEST,
      data: postUserId,
    })
  }, [])

  const onRemovePost = useCallback(postId => () => {
    dispatch({
      type: REMOVE_POST_REQUEST, 
      data: postId,
    })
  }, []);

    return(
      <div>
        <Card
        key={+post.createdAt}
        cover={post.Images && post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <TwitterOutlined key='retweet' onClick={onRetweet} />,
          <HeartOutlined key='heart' onClick={onLikeOrUnlike} />,
          <EditOutlined key="edit" onClick={onToggleComment} />,
          <Popover
            key='ellipsis'
            content={( 
              <Button.Group>
              {me && post.UserId
              ?(<>
                <Button>update</Button>
                <Button type='danger' onClick={onRemovePost(post.id)}>remove</Button>
              </>)
              :<Button>send a complaint</Button>}
              </Button.Group>
            )}
          >
             <EllipsisOutlined key="ellipsis" />
          </Popover>
         ,
        ]}
        title={post.Retweet && post.Retweet.User && post.Retweet.User.nickname && me && me.userId? `${me.userId} retweet ${post.Retweet.User.nickname}'s post`: null}
        extra={ !me || (post.User.id === me.id) 
          ? null
          : me.Followings && me.Followings.find(v => v.id === post.User.id)
            ? <Button onClick={onUnfollow(post.User.id)} >Unfollow</Button>
            : <Button onClick={onFollow(post.User.id)} >Follow</Button>}
        >
          {
            post.RetweetId && post.Retweet && post.Retweet.User && post.Retweet.User.id?
            (<Card
              cover={post.Retweet.Images && post.Retweet.Images[0] && <PostImages images={post.Retweet.Images} />}
            >
              <Card.Meta 
                avatar={<Link href={{ pathname: `/user`, query: {id: post.Retweet.User.id}}} as={`/user/${post.Retweet.User.id}`} >
                         <a><Avatar>{post.Retweet.User.nickname[0]}</Avatar></a>
                      </Link>}
                title={post.Retweet.User.nickname}
                description={<PostCardContent postData={post.Retweet.content} />}
              />
            </Card>
            ):(
          <Card.Meta 
          avatar={<Link href={{ pathname: `/user`, query: {id: post.User.id}}} as={`/user/${post.User.id}`} >
                   <a><Avatar>{post.User.nickname[0]}</Avatar></a>
                </Link>}
          title={post.User.nickname}
          description={<PostCardContent postData={post.content} />}
          />) 
          }        
        </Card>

        { commentFormOpened && 
          <>
            <form onSubmit={onSubmitComment} >
              <Form.Item>
                <Input.TextArea row={4} value={commentText} onChange={onChangeCommentText} />
              </Form.Item>
              <Button type="primary" htmlType="submit" loading={isAddingComment} >jack</Button>
            </form>
            <List 
              header={`comments: ${post.comments? post.comments.length: 0}`}
              itemLayout="horizontal"
              dataSource={post.comments || []}
              renderItem={item => (
                <li>
                  <Comment 
                  author={item.User.nickname}
                  avatar={<Link as={`/user/${item.User.id}`} href={{ pathname: `/user`, query: {id: item.User.id}}} >
                           <a><Avatar>{item.User.nickname[0]}</Avatar></a>
                          </Link>}
                  content={item.content}
                  />
                </li>
              )}

            />
          </>
        }
      </div>
    )
};

PostCard.propTypes = {
    card: PropTypes.shape({
        User: PropTypes.object,
        img: PropTypes.string,
        content: PropTypes.string,
        createdAt: PropTypes.object,
    })
}

export default PostCard;
