import React from 'react';
import Proptypes from 'prop-types';

import { PlusOutlined } from '@ant-design/icons';

const PostImage = ({ images }) => {
    if(post.length === 1){
        return(
            <img src={`http://localhost:3306/${images[0].src}`} alt={'post image'} />
        )
    }else if(post.length === 2){
        return(
            <div>
                <img src={`http://localhost:3306/${images[0].src}`} alt={'post image1'} width='50%' />
                <img src={`http://localhost:3306/${images[1].src}`} alt={'post image2'} width='50%' />
            </div>
        )
    }else{
        return(
            <div>
                <img src={`http://localhost:3306/${images[0].src}`} alt={'post image1'} />
                <div style={{display: 'inline-block', width:'50%', textAlign: 'center', verticalAlign:'middle'}}>
                    <PlusOutlined />
                    <br />
                    See more {images.length - 1} images
                </div>
            </div>
        )
    }
}

PostImage.proptypes = {
    post: Proptypes.arrayOf(Proptypes.object).isRequired,
}

export default PostImage;
