import React from 'react';
import { Input, Button, Card, Avatar } from 'antd';
import { EllipsisOutlined, EditOutlined, HeartOutlined, TwitterOutlined } from '@ant-design/icons';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [{
        user: {
            id: 1,
            nickname: 'zero',
            content: 'cho'
        }
    }],
}

const Home = () => {
    return (
        <div>
            { dummy.isLoggedIn &&
              <form>
                <Input.TextArea maxLength={140} placeholder='write something' />
                <div>
                    <input type='file' multiple hidden />
                    <Button>image upload</Button>
                    <Button type='primary' htmlType='submit' style={{float: 'right'}} >submit</Button>
                </div>
                <div>
                    { dummy.imagePaths.map((v) => {
                        return(
                            <div key={v} style={{ display: 'inline-block' }} >
                                <img  src={'http://localhost:3065'+ v} style={{ width: '200px'}} alt={v} />
                                <div>
                                    <Button>remove</Button>
                                </div>
                            </div>
                        )
                    })}
                </div>
              </form>}
              { dummy.mainPosts.map((c) => {
                  return(
                      <Card
                      key={+c.createdAt}
                      cover={c.img && <img src={c.img} alt='example' />}
                      actions={[
                        <TwitterOutlined key='retweet'/>,
                        <HeartOutlined key='heart' />,
                        <EditOutlined key="edit" />,
                        <EllipsisOutlined key="ellipsis" />,
                      ]}
                      extra={<Button>Follow</Button>}
                      >
                        <Card.Meta 
                        avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                        title={c.User.nickname}
                        decription={c.content}
                        />                          
                      </Card>
                  )
              })
              }
        </div>
    )
}

export default Home;
