import React, { useState } from 'react';
import Link from 'next/link';
import { Menu, Input, Button, Avatar, Card, Col, Row, Form } from 'antd';
import PropTypes from 'prop-types';

import LoginForm from './loginForm';

const dummy = {
    user: "oiry",
    posts:[],
    followers: [],
    followings: [],
    isLoggedIn: false,
}

const AppLayout = ({ children }) => {

  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
        <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
        <Menu.Item key="mail">
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
        </Menu.Item>
      </Menu>
      <Row gutter={10}>
          <Col xs={24} md={6}  >
            {dummy.isLoggedIn? 
            <Card
                actions = {[
                    <div key='post' >jack,jack<br/>{dummy.posts.length}</div>,
                    <div key='followers'>Followers<br/>{dummy.followers.length}</div>,
                    <div key='follwings'>Followings<br/>{dummy.followings.length}</div>
                ]}
            >
                <Card.Meta 
                    avatar={<Avatar>{dummy.user[0]}</Avatar>}
                    title={dummy.user}
                />
            </Card>:
            <LoginForm />
            }
          </Col>

          <Col xs={24} md={12} >
             {children}
          </Col>
          <Col xs={24} md={6} ></Col>
      </Row>

    </div>
  );
};

AppLayout.propTypes = {
    children: PropTypes.node,
}

export default AppLayout;
