import React, { useState} from 'react';
import PropTypes from 'prop-types';
import Slick from 'react-slick';
import styled from 'styled-components';

import { CloseOutlined } from '@ant-design/icons';


const Overlays = styled.div`
  position: fixed;
  zIndex: 5000;
  top: 0;
  lift: 0;
  right: 0;
  bottom: 0;
`
const Header = styled.header`
height: 44px;
background: white;
position: relative;
padding: 0;
text-align: center;

  & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }
`
const SlickWrapper = styled.div`
  height: calc(100% - 44px);
  background: 090909;
`
const CloseBtn = styled(CloseOutlined)`
  position: absolute;
  right: 0;
  top: 0;
  padding: 15px;
  line-height: 14px;
  cursor: pointer;
`
const ImgWrapper = styled.div`
  padding: 32; 
  textAlign: center;

  & img {
    margin: 0 auto;
    maxHeight: 750;
  }
`
const Indicator = styled.dev`
  textAlign: center;

  & > div {
    width: 75px;
    height: 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`

const ImageZoom = ({ images, onClose }) => {
    console.log('imageZoom: ', images)
  const [currentSlide, setCurrentSlide] = useState(0);

  return (
    <Overlays>
      <Header>
        <h1>상세 이미지</h1>
        <CloseBtn type="close" onClick={onClose} />
      </Header>
      <SlickWrapper>
        <div>
          <Slick
            initialSlide={0}
            afterChange={slide => setCurrentSlide(slide)}
            infinite={false}
            arrows
            slidesToShow={1}
            slidesToScroll={1}
          >
            {images.map((v) => {
              return (
                <ImgWrapper>
                  <img src={`http://localhost:3306/${v.src}`} />
                </ImgWrapper>
              );
            })}
          </Slick>
          <Indicator>
            <div>
              {currentSlide + 1} / {images.length}
            </div>
          </Indicator>
        </div>
      </SlickWrapper>
    </Overlays>
  );
};

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.shape({
    src: PropTypes.string,
  })).isRequired,
  onClose: PropTypes.func.isRequired,
};



export default ImagesZoom;
