const dummyUser = {
    nickname:'oiry',
    posts:[],
    followers: [],
    followings: [],
    signUpData: {},
}

export const initiatState = {
    isLoggedIn: false,
    user: null,
}

export const LOG_IN = 'LOG_IN'
export const LOG_OUT = 'LOG_OUT'
export const SIGN_UP = 'SIGN_UP';

export const loginAction = {
    type: LOG_IN,
    data: {
        nickname: 'oiry'
    }
}
export const logoutAction = {
    type: LOG_OUT,
}

export const signUpAction = (data) => {
    return{
        type: SIGN_UP,
        data: data
    }
}

const userReducer = (state=initiatState, action) => {
    switch(action.type){
        case LOG_IN:
            return{
                ...state,
                isLoggedIn: true,
                user: dummyUser,
            }
        case LOG_OUT:
            return{
                ...state,
                isLoggedIn: false,
                user: {}
            }
        case SIGN_UP:
            return{
                ...state,
                signUpData: action.data
            }
        default:
            return {
                ...state
            } 

    }
}

export default userReducer;
