import { all, put, call, takeLatest, fork, delay } from 'redux-saga/effects';
import axios from 'axios'
import { LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE, SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE } from '../reducers/user';


function loginAPI() {
    //requesting server part
}

function* login(){
    try{
        //yield call(loginAPI);
        yield delay(3000)
        yield put({
            type: LOG_IN_SUCCESS
        })
    }catch(err){
        console.error(err)
        yield put({
            type: LOG_IN_FAILURE,
        })
    }
}

function* watchLogin(){
    yield takeLatest(LOG_IN_REQUEST, login)

}

function signupAPI(signUpData){
    return axios.post('http://localhost:3306/api/user', signUpData);
}

function* signup(action){
    try{
        yield call(signupAPI, action.data);
        yield put({
            type: SIGN_UP_SUCCESS,
        })
    }catch(err){
        console.error(err)
        yield put({
            type: SIGN_UP_FAILURE,
            error: err,
        })
    }

}

function* watchSignup(){
    yield takeLatest(SIGN_UP_REQUEST, signup)
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchSignup),
    ])
}
