export const initiatState = {
    isLoggedIn: false,
    user: {
        nickname:'oiry',
        posts:[],
        followers: [],
        followings: [],
    },
}

export const LOG_IN = 'LOG_IN'
export const LOG_OUT = 'LOG_OUT'

export const loginAction = {
    type: LOG_IN,
    data: {
        nickname: 'oiry'
    }
}
export const logoutAction = {
    type: LOG_OUT,
}

const userReducer = (state=initiatState, action) => {
    switch(action.type){
        case LOG_IN:
            return{
                ...state,
                isLoggedIn: true,
                user: action.data,
            }
        case LOG_OUT:
            return{
                ...state,
                isLoggedIn: false,
                user: {}
            }
        default:
            return {
                ...state
            } 

    }
}

export default userReducer;
