import React from 'react';
import Proptypes from 'prop-types';

import { Icon } from 'antd';

const PostImage = ({ post }) => {
    if(post.length === 1){
        return(
            <img src={`http://localhost:3306/${post.src}`} alt={'post image'} />
        )
    }else if(post.length === 2){
        return(
            <div>
                <img src={`http://localhost:3306/${post[0].src}`} alt={'post image1'} width='50%' />
                <img src={`http://localhost:3306/${post[1].src}`} alt={'post image1'} />
            </div>
        )
    }else{
        return(
            <div>
                <img src={`http://localhost:3306/${post[0].src}`} alt={'post image1'} />
                <div style={{display: 'inline-block', width:'50%', textAlign: 'center', verticalAlign:'middle'}}>
                    <Icon type='plus' />
                    <br />
                    See more {post.length - 1} images
                </div>
            </div>
        )
    }
}

PostImage.proptypes = {
    post: Proptypes.array(Proptypes.object).isRequired,
}

export default PostImage;
