import React, { useCallback, useEffect } from 'react';
import { List, Button, Card } from 'antd';
import { StopOutlined } from '@ant-design/icons';
import NicknameEditForm from '../components/nicknameEditForm';
import { useDispatch, useSelector } from 'react-redux';

import PostCard from '../components/postCard';
import { LOAD_FOLLOWINGS_REQUEST, LOAD_FOLLOWERS_REQUEST,
        UNFOLLOW_USER_REQUEST, REMOVE_FOLLOWER_REQUEST } from '../reducers/user';
import { LOAD_USER_POSTS_REQUEST } from '../reducers/post';


const Profile = () => {
    const { me, followingList, followerList } = useSelector(state => state.user);
    const { mainPosts } = useSelector(state => state.post);
    const dispatch = useDispatch();

    const onUnfollow = useCallback(userId => () =>{
        dispatch({
            type: UNFOLLOW_USER_REQUEST,
            data: userId,
        })
    }, [])

    const onRemoveFollower = useCallback(userId => () => {
        dispatch({
            type: REMOVE_FOLLOWER_REQUEST, 
            data: userId,
        })
     }, [])

    const onLoadMoreFollowings = useCallback(() => {
        dispatch({
            type: LOAD_FOLLOWINGS_REQUEST,
            offset: followingList.length,
        })
    } ,[followingList.length]);

    const onLoadMoreFollowers = useCallback(() => {
        dispatch({
            type: LOAD_FOLLOWERS_REQUEST,
            offset: followerList.length,
        })
    }, [followerList.length]);

    return(
        <div>
            <NicknameEditForm />
            <List
                style={{ marginBottom: '20px' }}
                grid={{ gutter: 4, xs: 2, md: 3 }}
                size="small"
                header={<div>팔로잉 목록</div>}
                loadMore={<Button style={{ width: '100%' }} onClick={onLoadMoreFollowings} >더 보기</Button>}
                bordered
                dataSource={followingList}
                renderItem={item => (
                <List.Item style={{ marginTop: '20px' }}>
                    <Card actions={[<StopOutlined key='stop' onClick={onUnfollow(item.id)} />]}>
                        <Card.Meta description={item.nickname} />
                    </Card>
                </List.Item>
                )}
            />
            <List
                style={{ marginBottom: '20px' }}
                grid={{ gutter: 4, xs: 2, md: 3 }}
                size="small"
                header={<div>팔로워 목록</div>}
                loadMore={<Button style={{ width: '100%' }} onClick={onLoadMoreFollowers} >더 보기</Button>}
                bordered
                dataSource={followerList}
                renderItem={item => (
                <List.Item style={{ marginTop: '20px' }}>
                    <Card actions={[<StopOutlined key='stop' onClick={onRemoveFollower(item.id)} />]}>
                        <Card.Meta description={item.nickname} />
                    </Card>
                </List.Item>
                )}
            />
            <div>
            {
                mainPosts.map((v) => (
                    <PostCard post={v} key={+v.createdAt} />
                ))
            }
            </div>
        </div>
    )
}

Profile.getInitialProps = async (context) => {
    const state = context.store.getState();
    context.store.dispatch({
        type: LOAD_FOLLOWINGS_REQUEST,
        data: state.user.me && state.user.me.id,
    })
    context.store.dispatch({
        type: LOAD_FOLLOWERS_REQUEST,
        data: state.user.me && state.user.me.id,
    })
    context.store.dispatch({
        type: LOAD_USER_POSTS_REQUEST,
        data: state.user.me && state.user.me.id,
    })
}

export default Profile;
