import { all, put, call, takeLatest, fork } from 'redux-saga/effects';
import axios from 'axios'
import { LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE, 
        SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, 
        LOG_OUT_REQUEST, LOG_OUT_SUCCESS, LOG_OUT_FAILURE,
        LOAD_USER_REQUEST, LOAD_USER_SUCCESS, LOAD_USER_FAILURE, 
        FOLLOW_USER_REQUEST, FOLLOW_USER_SUCCESS, FOLLOW_USER_FAILURE, 
        UNFOLLOW_USER_REQUEST, UNFOLLOW_USER_SUCCESS, UNFOLLOW_USER_FAILURE } from '../reducers/user';


function loginAPI(loginData) {
    return axios.post('/user/login', loginData, {
        withCredentials: true,
    });
}

function* login(action){
    try{
        const result = yield call(loginAPI, action.data);
        yield put({
            type: LOG_IN_SUCCESS,
            data: result.data,
        })
    }catch(err){
        console.error(err)
        yield put({
            type: LOG_IN_FAILURE,
        })
    }
}

function* watchLogin(){
    yield takeLatest(LOG_IN_REQUEST, login)

}

function signupAPI(signUpData){
    return axios.post('/user', signUpData);
}

function* signup(action){
    try{
        yield call(signupAPI, action.data);
        yield put({
            type: SIGN_UP_SUCCESS,
        })
    }catch(err){
        console.error(err)
        yield put({
            type: SIGN_UP_FAILURE,
            error: err,
        })
    }

}

function* watchSignup(){
    yield takeLatest(SIGN_UP_REQUEST, signup)
}

function logoutAPI() {
    return axios.post('/user/logout', {}, {
        withCredentials: true,
    })
}

function* logout(){
    try{
        yield call(logoutAPI);
        yield put({
            type: LOG_OUT_SUCCESS,
        })
    }catch(err){
        console.error(err);
        yield put({
            type: LOG_OUT_FAILURE,
            error: err,
        })
    }
}

function* watchLogout(){
    yield takeLatest(LOG_OUT_REQUEST, logout)
}

function loadUserAPI(userId){
    return axios.get(userId? `/user/${userId}`: `/user`, {
        withCredentials: true,
    })
}

function* loadUser(action){
    try{
        const result = yield call(loadUserAPI, action.data);
        console.log('loadUserresult:', result);
        yield put({
            type: LOAD_USER_SUCCESS,
            data: result.data,
            me: !action.data,
        })
    }catch(err){
        console.error(err);
        yield put({
            type: LOAD_USER_FAILURE,
            error: err,
        })
    }
}

function* watchLoadUser(){
    yield takeLatest(LOAD_USER_REQUEST, loadUser)
}

function followUserAPI(postUserId){
    return axios.post(`/user/${postUserId}/follow`, {}, {
        withCredentials: true,
    })
}

function* followUser(action){
    try{
        const result = yield call(followUserAPI, action.data);
        yield put({
            type: FOLLOW_USER_SUCCESS,
            data: result.data,
        })
    }catch(e){
        console.error(e);
        yield put({
            type: FOLLOW_USER_FAILURE,
            error: e,
        })
    }
}

function* watchFollowUser(){
    yield takeLatest(FOLLOW_USER_REQUEST, followUser);
}

function unfollowUserAPI(postUserId){
    return axios.delete(`/user/${postUserId}/follow`);
}

function* unfollowUser(action){
    try{
        const result = yield call(unfollowUserAPI, action.data);
        yield put({
            type: UNFOLLOW_USER_SUCCESS,
            data: result.data,
        })
    }catch(e){
        console.error(e),
        yield put({
            type: UNFOLLOW_USER_FAILURE,
            error: e,
        })
    }
}

function* watchUnfollowUser(){
    yield takeLatest(UNFOLLOW_USER_REQUEST, unfollowUser);
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchLogout),
        fork(watchSignup),
        fork(watchLoadUser),
        fork(watchFollowUser),
        fork(watchUnfollowUser),
    ])
}
