import React, { useState } from 'react';
import AppLayout from '../components/appLayout';
import Head from 'next/head';
import { Form, Checkbox, Input, Button } from 'antd';

const Signup = () => {
    const [ id, setId ] = useState('');
    const [ nick, setNick ] = useState('');
    const [ password, setPassword ] = useState('');
    const [ passwordCheck, setPasswordCheck ] = useState('');
    const [ term, setTerm ] = useState(false);
    const [ passwordCheckError, setPasswordCheckError ] = useState(false);
    const [ termError, setTermError] = useState(false);

    const onSubmit = useCallback((e) => {
        e.preventDefault();

        if(password !== passwordCheck){
            return setPasswordCheckError(true);
        }
        if(!term){
            return setTermError(true);
        }

    }, [ password, passwordCheck, term])
    const onChangeId = useCallback((e) => {
        setId(e.target.value)
    }, [])
    const onChangeNick = useCallback((e) => {
        setNick(e.target.value);
    }, [ ])
    const onChangePassword = (e) => {
        setPassword(e.target.value);
    }
    const onChangePasswordCheck = useCallback((e) => {
        setPasswordCheckError(e.target.value !== password);
        setPasswordCheck(e.target.value);
    }, [password])
    const onChangeTerm = useCallback((e) => {
        setTermError(false); 
        setTerm(e.target.checked);
    }, [])

    return(
        <>
            <Head>
                <title>Nodebird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.css" />
            </ Head>
            <AppLayout>
                <form onSubmit={onSubmit} style={{ padding: 10}} >
                <div>
                    <label htmlFor="user-id" >id</label>
                    <br />
                    <Input name="user-id" value={id} required onChange={onChangeId} />
                </div>
                <div>
                    <label htmlFor="user-nick" >nickname</label>
                    <br />
                    <Input name="user-nick" value={nick} required onChange={onChangeNick} />
                </div>
                <div>
                    <label htmlFor="user-password">password</label>
                    <br />
                    <Input name="user-password"  value={password} type="password" required onChange={onChangePassword} />
                </div>
                <div>
                    <label htmlFor="user-password-check">password-check</label>
                    <br />
                    <Input name="user-password-check" value={passwordCheck} type="password" required onChange={onChangePasswordCheck} />
                </div>
                { passwordCheckError && <div style={{color: 'red'}} >dismatch the password</div>}
                <div>
                    <Checkbox name="user-term" checked={term} onChange={onChangeTerm} >Are you sure??</Checkbox>
                </div>
                { termError && <div style={{color: 'red'}} >you should agree with the term</div>}
                <div style={{ marginTop: 10 }} >
                    <Button type="primary" htmlType="submit" >Signup</Button>
                </div>
                </form>
            </AppLayout>
        </>
    )
}

export default Signup;
