import React from 'react';
import { Card, Button, Avatar,} from 'antd';
import PropTypes from 'prop-types';

import { EllipsisOutlined, EditOutlined, HeartOutlined, TwitterOutlined } from '@ant-design/icons';


const PostCard = ({ card } ) => {
    return(
        <Card
        key={+card.createdAt}
        cover={card.img && <img src={card.img} alt='example' />}
        actions={[
          <TwitterOutlined key='retweet'/>,
          <HeartOutlined key='heart' />,
          <EditOutlined key="edit" />,
          <EllipsisOutlined key="ellipsis" />,
        ]}
        extra={<Button>Follow</Button>}
        >
          <Card.Meta 
          avatar={<Avatar>{card.user.nickname[0]}</Avatar>}
          title={card.user.nickname}
          decription={card.user.content}
          />                          
        </Card>
    )
};

PostCard.propTypes = {
    card: PropTypes.shape({
        User: PropTypes.object,
        img: PropTypes.string,
        content: PropTypes.string,
        createdAt: PropTypes.object,
    })
}

export default PostCard;
