import React from 'react';
import { useSelector } from 'react-redux';
import Proptypes from 'prop-types';
import { LOAD_POST_REQUEST } from '../reducers/post';

const SinglePost = ({ }) => {
    const { singlePost } = useSelector(state => state.post);
    return(
        <>
            <div>{singlePost.User && singlePost.User.nickname}</div>
            <div>{singlePost.content && singlePost.content}</div>
            <div>{singlePost.Images[0] && <img src={`http://localhost:3060/${singlePost.Images[0].src}`}/>}</div>
        </>
    )
}

SinglePost.getInitialProps = async (context) => {
    context.store.dispatch({
        type: LOAD_POST_REQUEST,
        data: context.query.id,
    })
}

// SinglePost.prototypes = {
//     id: Proptypes.number.isRequired,
// }

export default SinglePost;
