import { all, fork, call, takeLatest, put, delay } from 'redux-saga/effects';
import axios from 'axios';
import { ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE, 
        ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE,
        LOAD_MAIN_POSTS_REQUEST, LOAD_MAIN_POSTS_SUCCESS, LOAD_MAIN_POSTS_FAILURE, 
        LOAD_HASHTAG_POSTS_REQUEST, LOAD_HASHTAG_POSTS_SUCCESS, LOAD_HASHTAG_POSTS_FAILURE,
        LOAD_USER_POSTS_REQUEST, LOAD_USER_POSTS_SUCCESS, LOAD_USER_POSTS_FAILURE, 
        LOAD_COMMENTS_REQUEST, LOAD_COMMENTS_SUCCESS, LOAD_COMMENTS_FAILURE } from '../reducers/post';

function addPostAPI(postData){
    return axios.post('/post', postData, {
        withCredentials: true,
    })
}

function* addPost(action){
    try{
        const result = yield call(addPostAPI, action.data);
        console.log('addPostResult: ', result)
        yield put({
            type: ADD_POST_SUCCESS,
            data: result.data,
        });
    }catch(err){
        console.error(err);
        yield put({
            type: ADD_POST_FAILURE,
            error: err,
        })
    }
}

function* watchAddPost(){
    yield takeLatest(ADD_POST_REQUEST, addPost)
}

function addCommentAPI(data){
    return axios.post(`post/${data.postId}/comment`, { content: data.content }, {
        withCredentials: true,
    })
}

function* addComment(action){
    try{
        const result = yield call(addCommentAPI, action.data);
        yield put({
            type: ADD_COMMENT_SUCCESS,
            data: {
                postId: action.data.postId,
                comment: result.data,
            }
        })
    }catch(err){
        console.error(err);
        yield put({
            type: ADD_COMMENT_FAILURE,
            error: action.error,
        })
    }
}

function* watchAddComment(){
    yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}

function loadCommentsAPI(postId){
    return axios.get(`/post/${postId}/comments`);
}

function* loadComments(action) {
    try {
      const result = yield call(loadCommentsAPI, action.data);
      yield put({
        type: LOAD_COMMENTS_SUCCESS,
        data: {
          postId: action.data,
          comments: result.data,
        },
      });
    } catch (e) {
      console.error(e);
      yield put({
        type: LOAD_COMMENTS_FAILURE,
        error: e,
      });
    }
  }

function* watchLoadComments(){
    yield takeLatest(LOAD_COMMENTS_REQUEST, loadComments)
}

function loadMainPostAPI(){
    return axios.get('/posts');
}

function* loadMainPost(){
    try{
        const result = yield call(loadMainPostAPI);
        yield put({
            type: LOAD_MAIN_POSTS_SUCCESS,
            data: result.data,
        })
    }catch(err){
        console.error(err);
        yield put({
            type: LOAD_MAIN_POSTS_FAILURE,
            error: err,
        })
    }
}

function* watchLoadMainPost(){
    yield takeLatest(LOAD_MAIN_POSTS_REQUEST, loadMainPost);
}

function loadHashtagPostsAPI(tag){
    return axios.get(`/hashtag/${tag}`)
}

function* loadHashtagPosts(action){
    try{
        const result = yield call(loadHashtagPostsAPI, action.data);
        yield put({
            type: LOAD_HASHTAG_POSTS_SUCCESS,
            data: result.data,
        })
    }catch(e){
        console.error(e),
        yield put({
            type: LOAD_HASHTAG_POSTS_FAILURE,
            error: e,
        })
    }
}

function* watchLoadHashtagPosts(){
    yield takeLatest(LOAD_HASHTAG_POSTS_REQUEST, loadHashtagPosts)
}

function loadUserPostsAPI(id){
    return axios.get(`user/${id}/posts`)
}

function* loadUserPosts(action){
    try{
        const result = yield call(loadUserPostsAPI, action.data);
        yield put({
            type: LOAD_USER_POSTS_SUCCESS,
            data: result.data,
        })
    }catch(e){
        console.error(e);
        yield put({
            type: LOAD_USER_POSTS_FAILURE,
            error: e,
        })
    }
}

function* watchLoadUserPosts(){
    yield takeLatest(LOAD_USER_POSTS_REQUEST, loadUserPosts);
}

export default function* postSaga() {
    yield all([
        fork(watchAddPost),
        fork(watchAddComment),
        fork(watchLoadComments),
        fork(watchLoadMainPost),
        fork(watchLoadHashtagPosts),
        fork(watchLoadUserPosts),
    ])
}
