import React from 'react';
import { Input, Button } from 'antd';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [],
}

const Home = () => {
    return (
        <div>
            { isLoggedIn &&
              <form>
                <Input.text maxLength={140} placeholder={'write something'} />
                <div>
                    <input type='file' multiple hidden />
                    <Button>image upload</Button>
                    <Button type='primary' htmlType='submit' style={{float: 'right'}} >submit</Button>
                </div>
                <div>
                    { dummy.imagePaths.map((v, i) => {
                        return(
                            <div key={v} style={{ display: 'inline-block' }} >
                                <img  src={'http://localhost:3065'+ v} style={{ with: '200px'}} alt={v} />
                                <div>
                                    <Button>remove</Button>
                                </div>
                            </div>
                        )
                    })

                    }
                </div>
              </form>}
              { dummy.mainPosts.map((c) => {
                  return(
                      <Card
                      key={+c.createdAt}
                      cover={c.img && <img src={c.img} alt='example' />}
                      actions={[
                        <Icon type='retweet' key='retweet' />,
                        <Icon type='heart' key='heart' />,
                        <Icon type='message' key='message' />,
                        <Icon type='ellipsis' key='ellipsis' />
                      ]}
                      extra={<Button>Follow</Button>}
                      >
                        <Card.Meta 
                        avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                        title={c.User.nickname}
                        decription={c.content}
                        />                          
                      </Card>
                  )
              })

              }
        </div>
    )
}

export default Home;
